	`identityImg` varchar(255) default '' comment '身份证照',
	`businessImg` varchar(255) default '' comment '营业执照',
	`remark` text comment '用户备注(专家介绍)',
	`spreaderUserId` int default 0 comment '所属推广人用户id',
	`spreaderNickName` varchar(255) default '' comment '所属推广人昵称',
	`spreaderRealName` varchar(255) default '' comment '所属推广人真实姓名',
